import React, { useState } from 'react';
import { 
  ShoppingBag, 
  Heart, 
  Settings, 
  MapPin,
  Eye,
  Calendar,
  Package,
  Store,
  Plus,
  Award
} from 'lucide-react';

const UserProfile = () => {
  const [activeTab, setActiveTab] = useState('orders');

  // Mock user data - in real app this would come from API/auth context
  const userData = {
    id: 'user_123',
    firstName: 'John',
    lastName: 'Doe',
    email: 'john.doe@example.com',
    phone: '+1 (555) 123-4567',
    avatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=100&h=100&fit=crop&crop=face',
    location: {
      city: 'New York',
      state: 'NY',
      country: 'USA'
    },
    accountType: 'buyer', // 'buyer' or 'seller'
    canSell: false, // true after upgrade
    isSeller: false, // true if user has selling capabilities
    stats: {
      totalPurchases: 12,
      totalSpent: 850,
      favoriteItems: 23,
      reviewsGiven: 8,
      rating: 4.6, // as a buyer
      reviewCount: 5
    },
    joinedDate: '2023-06-15',
    membershipLevel: 'Silver', // Bronze, Silver, Gold, Platinum
    isVerified: true
  };

  // Tab configuration - different tabs for sellers
  const getTabConfig = () => {
    if (userData.isSeller) {
      // Seller tabs
      return [
        { id: 'overview', label: 'Overview', icon: ShoppingBag },
        { id: 'management', label: 'Management', icon: Settings },
        { id: 'dashboard', label: 'Dashboard', icon: Eye },
        { id: 'products', label: 'My Products', icon: Package },
        { id: 'settings', label: 'Settings', icon: Heart }
      ];
    } else {
      // Buyer tabs
      return [
        { id: 'orders', label: 'Order History', icon: ShoppingBag },
        { id: 'pending', label: 'Pending Orders', icon: Package },
        { id: 'wishlist', label: 'Wishlist', icon: Heart },
        { id: 'settings', label: 'Settings', icon: Settings }
      ];
    }
  };

  const tabConfig = getTabConfig();



  const handleUpgradeToSeller = () => {
    // In real app, this would make API call to upgrade account
    // For now, just update the user state to seller mode
    userData.isSeller = true;
    userData.canSell = true;
    setActiveTab('overview'); // Go to seller overview tab
    window.location.reload(); // Simple refresh to update UI
  };

  // For testing - toggle between buyer and seller views
  const handleToggleSellerMode = () => {
    // In real app, this would come from API/auth context
    // For demo purposes, we'll toggle the seller state
    userData.isSeller = !userData.isSeller;
    userData.canSell = userData.isSeller;
    
    // Set appropriate default tab after toggle
    if (userData.isSeller) {
      setActiveTab('overview');
    } else {
      setActiveTab('orders');
    }
    
    window.location.reload(); // Simple refresh for demo
  };

  return (
    <div className="min-h-screen bg-secondary-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between py-6">
            <div className="flex items-center space-x-4">
              <img 
                src={userData.avatar} 
                alt={`${userData.firstName} ${userData.lastName}`}
                className="w-16 h-16 rounded-full object-cover"
              />
              <div>
                <h1 className="text-2xl font-bold text-secondary-800">
                  My Profile
                </h1>
                <div className="flex items-center space-x-4 text-sm text-secondary-600">
                  <span className="flex items-center">
                    <MapPin className="h-4 w-4 mr-1" />
                    {userData.location.city}, {userData.location.state}
                  </span>
                  <span className="flex items-center">
                    <Award className="h-4 w-4 mr-1" />
                    {userData.membershipLevel} Member
                  </span>
                  <span className="flex items-center">
                    <Calendar className="h-4 w-4 mr-1" />
                    Joined {new Date(userData.joinedDate).toLocaleDateString()}
                  </span>
                  {userData.isVerified && (
                    <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                      âœ“ Verified
                    </span>
                  )}
                </div>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              {/* Test Toggle Button - for demo purposes */}
              <button 
                onClick={handleToggleSellerMode}
                className="btn-secondary text-sm border-dashed"
              >
                {userData.isSeller ? 'ðŸ‘¤ Switch to Buyer View' : 'ðŸª Test Seller View'}
              </button>

              {!userData.canSell && !userData.isSeller && (
                <button 
                  onClick={handleUpgradeToSeller}
                  className="btn-primary text-sm bg-gradient-to-r from-primary-600 to-purple-600 hover:from-primary-700 hover:to-purple-700"
                >
                  <Store className="w-4 h-4 mr-2" />
                  Upgrade to Seller
                </button>
              )}

              {userData.isSeller && (
                <div className="flex items-center text-sm text-green-600">
                  <Store className="w-4 h-4 mr-1" />
                  <span className="font-medium">Seller Account</span>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>



              {/* Navigation Tabs */}
        <div className="bg-white border-b">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <nav className="flex space-x-8 overflow-x-auto">
              {tabConfig.map((tab) => {
                const Icon = tab.icon;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm whitespace-nowrap ${
                      activeTab === tab.id
                        ? 'border-primary-500 text-primary-600'
                        : 'border-transparent text-secondary-500 hover:text-secondary-700 hover:border-secondary-300'
                    }`}
                  >
                    <Icon size={18} />
                    <span>{tab.label}</span>
                  </button>
                );
              })}
            </nav>
          </div>
        </div>

              {/* Content */}
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
          {/* Seller Tabs */}
          {activeTab === 'overview' && userData.isSeller && (
            <SellerOverviewTab userId={userData.id} />
          )}

          {activeTab === 'management' && userData.isSeller && (
            <ManagementTab userId={userData.id} />
          )}

          {activeTab === 'dashboard' && userData.isSeller && (
            <DashboardTab userId={userData.id} />
          )}

          {activeTab === 'products' && userData.isSeller && (
            <MyProductsTab userId={userData.id} />
          )}

          {/* Buyer Tabs */}
          {activeTab === 'orders' && !userData.isSeller && (
            <OrderHistoryTab userId={userData.id} />
          )}

          {activeTab === 'pending' && !userData.isSeller && (
            <PendingOrdersTab userId={userData.id} />
          )}

          {activeTab === 'wishlist' && !userData.isSeller && (
            <WishlistTab userId={userData.id} />
          )}

          {/* Common Tabs */}
          {activeTab === 'settings' && (
            <SettingsTab userData={userData} />
          )}
        </div>


    </div>
  );
};



// Order History Tab
const OrderHistoryTab = ({ userId }) => {
  const [statusFilter, setStatusFilter] = useState('all');

  return (
    <div className="space-y-6">
      {/* Filters */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between space-y-4 md:space-y-0">
        <div className="flex items-center space-x-4">
          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="px-3 py-2 border border-secondary-300 rounded-lg"
          >
            <option value="all">All Orders</option>
            <option value="delivered">Delivered</option>
            <option value="shipping">Shipping</option>
            <option value="pending">Pending</option>
            <option value="cancelled">Cancelled</option>
          </select>
        </div>
        
        <button className="btn-primary flex items-center">
          <Plus className="w-4 h-4 mr-2" />
          Continue Shopping
        </button>
      </div>

      {/* Orders List */}
      <div className="space-y-4">
        {[1, 2, 3, 4, 5].map(i => (
          <div key={i} className="card p-6">
            <div className="flex items-center justify-between mb-4">
              <div>
                <h3 className="font-semibold text-secondary-800">Order #{12340 + i}</h3>
                <p className="text-sm text-secondary-600">Placed on Dec {i}, 2023</p>
              </div>
              <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                i <= 2 ? 'bg-green-100 text-green-800' : 
                i === 3 ? 'bg-blue-100 text-blue-800' : 
                'bg-yellow-100 text-yellow-800'
              }`}>
                {i <= 2 ? 'Delivered' : i === 3 ? 'Shipping' : 'Pending'}
              </span>
            </div>
            
            <div className="flex items-center space-x-4">
              <img 
                src={`https://images.unsplash.com/photo-156042235${i}-b33ff0c44a43?w=80&h=80&fit=crop`}
                alt="Product"
                className="w-16 h-16 rounded-lg object-cover"
              />
              <div className="flex-1">
                <h4 className="font-medium text-secondary-800">Product Name {i}</h4>
                <p className="text-sm text-secondary-600">Quantity: 1</p>
                <p className="text-lg font-bold text-primary-600">$99.99</p>
              </div>
              
              <div className="flex flex-col space-y-2">
                <button className="btn-secondary text-sm">
                  View Details
                </button>
                {i <= 2 && (
                  <button className="btn-outline text-sm">
                    Write Review
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Seller Overview Tab (for sellers)
const SellerOverviewTab = ({ userId }) => {
  return (
    <div className="space-y-6">
      <div className="flex items-center justify-between">
        <h2 className="text-xl font-semibold text-secondary-800">Sales Overview</h2>
        <div className="flex items-center space-x-4">
          <button className="btn-primary">
            <Plus className="w-4 h-4 mr-2" />
            List New Product
          </button>
        </div>
      </div>

      {/* Welcome Banner */}
      <div className="bg-gradient-to-r from-green-50 to-emerald-50 border border-green-200 rounded-lg p-6">
        <h3 className="font-semibold text-green-800 mb-3">ðŸŽ‰ Welcome to your seller dashboard!</h3>
        <p className="text-green-700 text-sm">
          Start listing your products and join thousands of successful sellers. Your journey to earning begins here!
        </p>
      </div>

      {/* Key Stats Cards */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <div className="card p-6 text-center">
          <div className="w-12 h-12 bg-green-100 rounded-lg flex items-center justify-center mx-auto mb-3">
            <Store className="h-6 w-6 text-green-600" />
          </div>
          <h3 className="text-lg font-semibold text-secondary-800 mb-1">Total Earnings</h3>
          <p className="text-3xl font-bold text-green-600">$0.00</p>
          <p className="text-sm text-secondary-600">Ready to start!</p>
        </div>
        
        <div className="card p-6 text-center">
          <div className="w-12 h-12 bg-blue-100 rounded-lg flex items-center justify-center mx-auto mb-3">
            <Package className="h-6 w-6 text-blue-600" />
          </div>
          <h3 className="text-lg font-semibold text-secondary-800 mb-1">Active Listings</h3>
          <p className="text-3xl font-bold text-blue-600">0</p>
          <p className="text-sm text-secondary-600">List your first product</p>
        </div>
        
        <div className="card p-6 text-center">
          <div className="w-12 h-12 bg-purple-100 rounded-lg flex items-center justify-center mx-auto mb-3">
            <ShoppingBag className="h-6 w-6 text-purple-600" />
          </div>
          <h3 className="text-lg font-semibold text-secondary-800 mb-1">Items Sold</h3>
          <p className="text-3xl font-bold text-purple-600">0</p>
          <p className="text-sm text-secondary-600">Make your first sale</p>
        </div>
        
        <div className="card p-6 text-center">
          <div className="w-12 h-12 bg-yellow-100 rounded-lg flex items-center justify-center mx-auto mb-3">
            <Eye className="h-6 w-6 text-yellow-600" />
          </div>
          <h3 className="text-lg font-semibold text-secondary-800 mb-1">Profile Views</h3>
          <p className="text-3xl font-bold text-yellow-600">12</p>
          <p className="text-sm text-secondary-600">People viewing your profile</p>
        </div>
      </div>

      {/* Quick Actions Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div className="card p-6">
