import React, { useState } from 'react';
import { 
  ShoppingBag, 
  Heart, 
  Settings, 
  MapPin,
  Eye,
  Calendar,
  Package,
  Store,
  Plus,
  Award
} from 'lucide-react';

const UserProfile = () => {
  // Mock user data - in real app this would come from API/auth context
  const [userData, setUserData] = useState({
    id: 'user_123',
    firstName: 'John',
    lastName: 'Doe',
    email: 'john.doe@example.com',
    phone: '+1 (555) 123-4567',
    avatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=100&h=100&fit=crop&crop=face',
    location: {
      city: 'New York',
      state: 'NY',
      country: 'USA'
    },
    accountType: 'buyer', // 'buyer' or 'seller'
    canSell: false, // true after upgrade
    isSeller: false, // true if user has selling capabilities
    stats: {
      totalPurchases: 12,
      totalSpent: 850,
      favoriteItems: 23,
      reviewsGiven: 8,
      rating: 4.6, // as a buyer
      reviewCount: 5
    },
    joinedDate: '2023-06-15',
    membershipLevel: 'Silver', // Bronze, Silver, Gold, Platinum
    isVerified: true
  });

  const [activeTab, setActiveTab] = useState('orders');

  // Tab configuration - different tabs for sellers
  const getTabConfig = () => {
    if (userData.isSeller) {
      // Seller tabs
      return [
        { id: 'dashboard', label: 'Dashboard', icon: Eye },
        { id: 'management', label: 'Management', icon: Settings },
        { id: 'products', label: 'My Products', icon: Package },
        { id: 'settings', label: 'Settings', icon: Heart }
      ];
    } else {
      // Buyer tabs
      return [
        { id: 'orders', label: 'Order History', icon: ShoppingBag },
        { id: 'pending', label: 'Pending Orders', icon: Package },
        { id: 'wishlist', label: 'Wishlist', icon: Heart },
        { id: 'settings', label: 'Settings', icon: Settings }
      ];
    }
  };

  const tabConfig = getTabConfig();



  const handleUpgradeToSeller = () => {
    // In real app, this would make API call to upgrade account
    // For now, just update the user state to seller mode
    setUserData(prev => ({
      ...prev,
      isSeller: true,
      canSell: true
    }));
    setActiveTab('dashboard'); // Go to seller dashboard tab
  };

  // For testing - toggle between buyer and seller views
  const handleToggleSellerMode = () => {
    // In real app, this would come from API/auth context
    // For demo purposes, we'll toggle the seller state
    const newIsSeller = !userData.isSeller;
    
    setUserData(prev => ({
      ...prev,
      isSeller: newIsSeller,
      canSell: newIsSeller
    }));
    
    // Set appropriate default tab after toggle
    if (newIsSeller) {
      setActiveTab('dashboard');
    } else {
      setActiveTab('orders');
    }
  };

  return (
    <div className="min-h-screen bg-secondary-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between py-6">
            <div className="flex items-center space-x-4">
              <img 
                src={userData.avatar} 
                alt={`${userData.firstName} ${userData.lastName}`}
                className="w-16 h-16 rounded-full object-cover"
              />
              <div>
                <h1 className="text-2xl font-bold text-secondary-800">
                  My Profile
                </h1>
                <div className="flex items-center space-x-4 text-sm text-secondary-600">
                  <span className="flex items-center">
                    <MapPin className="h-4 w-4 mr-1" />
                    {userData.location.city}, {userData.location.state}
                  </span>
                  <span className="flex items-center">
                    <Award className="h-4 w-4 mr-1" />
                    {userData.membershipLevel} Member
                  </span>
                  <span className="flex items-center">
                    <Calendar className="h-4 w-4 mr-1" />
                    Joined {new Date(userData.joinedDate).toLocaleDateString()}
                  </span>
                  {userData.isVerified && (
                    <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                      âœ“ Verified
                    </span>
                  )}
                </div>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              {/* Test Toggle Button - for demo purposes */}
              <button 
                onClick={handleToggleSellerMode}
                className="btn-secondary text-sm border-dashed"
              >
                {userData.isSeller ? 'ðŸ‘¤ Switch to Buyer View' : 'ðŸª Test Seller View'}
              </button>

              {!userData.canSell && !userData.isSeller && (
                <button 
                  onClick={handleUpgradeToSeller}
                  className="btn-primary text-sm bg-gradient-to-r from-primary-600 to-purple-600 hover:from-primary-700 hover:to-purple-700"
                >
                  <Store className="w-4 h-4 mr-2" />
                  Upgrade to Seller
                </button>
              )}

              {userData.isSeller && (
                <div className="flex items-center text-sm text-green-600">
                  <Store className="w-4 h-4 mr-1" />
                  <span className="font-medium">Seller Account</span>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>



              {/* Navigation Tabs */}
        <div className="bg-white border-b">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <nav className="flex space-x-8 overflow-x-auto">
              {tabConfig.map((tab) => {
                const Icon = tab.icon;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm whitespace-nowrap ${
                      activeTab === tab.id
