import React, { useState } from 'react';
import { 
  User, 
  ShoppingBag, 
  Heart, 
  MessageCircle, 
  Settings, 
  Star,
  MapPin,
  Phone,
  Mail,
  Eye,
  Calendar,
  Package,
  CreditCard,
  Award,
  Store,
  Plus
} from 'lucide-react';

const UserProfile = () => {
  // Set default active tab based on user type
  const getDefaultTab = () => {
    // Check if user is seller from URL or localStorage (in real app)
    const isSeller = false; // This would come from user state/context
    return isSeller ? 'overview' : 'orders';
  };

  const [activeTab, setActiveTab] = useState(getDefaultTab());

  // Mock user data - in real app this would come from API/auth context
  const userData = {
    id: 'user_123',
    firstName: 'John',
    lastName: 'Doe',
    email: 'john.doe@example.com',
    phone: '+1 (555) 123-4567',
    avatar: 'https://images.unsplash.com/photo-1472099645785-5658abf4ff4e?w=100&h=100&fit=crop&crop=face',
    location: {
      city: 'New York',
      state: 'NY',
      country: 'USA'
    },
    accountType: 'buyer', // 'buyer' or 'seller'
    canSell: false, // true after upgrade
    isSeller: false, // true if user has selling capabilities
    stats: {
      totalPurchases: 12,
      totalSpent: 850,
      favoriteItems: 23,
      reviewsGiven: 8,
      rating: 4.6, // as a buyer
      reviewCount: 5
    },
    joinedDate: '2023-06-15',
    membershipLevel: 'Silver', // Bronze, Silver, Gold, Platinum
    isVerified: true
  };

  // Tab configuration based on user type
  const getTabConfig = () => {
    const baseTabs = [
      { id: 'orders', label: 'Order History', icon: ShoppingBag },
      { id: 'pending', label: 'Pending Orders', icon: Package },
      { id: 'wishlist', label: 'Wishlist', icon: Heart },
      { id: 'settings', label: 'Settings', icon: Settings }
    ];

    // Only show Overview tab for sellers
    if (userData.isSeller) {
      return [
        { id: 'overview', label: 'Overview', icon: User },
        ...baseTabs
      ];
    }

    return baseTabs;
  };

  const tabConfig = getTabConfig();

  const statsCards = [
    {
      title: 'Total Orders',
      value: userData.stats.totalPurchases,
      icon: ShoppingBag,
      color: 'blue'
    },
    {
      title: 'Total Spent',
      value: userData.stats.totalSpent,
      icon: CreditCard,
      color: 'green',
      prefix: '$'
    },
    {
      title: 'Wishlist Items',
      value: userData.stats.favoriteItems,
      icon: Heart,
      color: 'red'
    },
    {
      title: 'Reviews Given',
      value: userData.stats.reviewsGiven,
      icon: Star,
      color: 'yellow'
    }
  ];

  const handleUpgradeToSeller = () => {
    // In real app, this would make API call to upgrade account
    // For now, redirect to simple seller dashboard
    window.location.href = '/sell/dashboard';
  };

  return (
    <div className="min-h-screen bg-secondary-50">
      {/* Header */}
      <div className="bg-white shadow-sm border-b">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex items-center justify-between py-6">
            <div className="flex items-center space-x-4">
              <img 
                src={userData.avatar} 
                alt={`${userData.firstName} ${userData.lastName}`}
                className="w-16 h-16 rounded-full object-cover"
              />
              <div>
                <h1 className="text-2xl font-bold text-secondary-800">
                  My Profile
                </h1>
                <div className="flex items-center space-x-4 text-sm text-secondary-600">
                  <span className="flex items-center">
                    <MapPin className="h-4 w-4 mr-1" />
                    {userData.location.city}, {userData.location.state}
                  </span>
                  <span className="flex items-center">
                    <Award className="h-4 w-4 mr-1" />
                    {userData.membershipLevel} Member
                  </span>
                  <span className="flex items-center">
                    <Calendar className="h-4 w-4 mr-1" />
                    Joined {new Date(userData.joinedDate).toLocaleDateString()}
                  </span>
                  {userData.isVerified && (
                    <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                       Verified
                    </span>
                  )}
                </div>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              {!userData.canSell && (
                <button 
                  onClick={handleUpgradeToSeller}
                  className="btn-primary text-sm bg-gradient-to-r from-primary-600 to-purple-600 hover:from-primary-700 hover:to-purple-700"
                >
                  <Store className="w-4 h-4 mr-2" />
                  Start Selling
                </button>
              )}
            </div>
          </div>
        </div>
      </div>



              {/* Navigation Tabs */}
        <div className="bg-white border-b">
          <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
            <nav className="flex space-x-8 overflow-x-auto">
              {tabConfig.map((tab) => {
                const Icon = tab.icon;
                return (
                  <button
                    key={tab.id}
                    onClick={() => setActiveTab(tab.id)}
                    className={`flex items-center space-x-2 py-4 px-1 border-b-2 font-medium text-sm whitespace-nowrap ${
                      activeTab === tab.id
                        ? 'border-primary-500 text-primary-600'
                        : 'border-transparent text-secondary-500 hover:text-secondary-700 hover:border-secondary-300'
                    }`}
                  >
                    <Icon size={18} />
                    <span>{tab.label}</span>
                  </button>
                );
              })}
            </nav>
          </div>
        </div>

      {/* Content */}
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        {activeTab === 'overview' && (
          <OverviewTab statsCards={statsCards} userData={userData} />
        )}

        {activeTab === 'orders' && (
          <OrderHistoryTab userId={userData.id} />
        )}

        {activeTab === 'pending' && (
          <PendingOrdersTab userId={userData.id} />
        )}

        {activeTab === 'wishlist' && (
          <WishlistTab userId={userData.id} />
        )}

        {activeTab === 'settings' && (
          <SettingsTab userData={userData} />
        )}
      </div>


    </div>
  );
};

// Overview Tab Component (Simple Buyer Profile)
const OverviewTab = ({ statsCards, userData }) => (
  <div className="space-y-8">
    {/* Stats Cards */}
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
      {statsCards.map((card, index) => {
        const Icon = card.icon;
        return (
          <div key={index} className="card p-6">
            <div className="flex items-center justify-between">
              <div>
                <p className="text-sm text-secondary-600">{card.title}</p>
                <p className="text-3xl font-bold text-secondary-800">
                  {card.prefix}{card.value}
                </p>
              </div>
              <div className={`p-3 rounded-lg bg-${card.color}-100`}>
                <Icon className={`h-6 w-6 text-${card.color}-600`} />
              </div>
            </div>
          </div>
        );
      })}
    </div>

    {/* Quick Actions for Buyers */}
    <div className="card p-6">
      <h3 className="text-lg font-semibold text-secondary-800 mb-4">Quick Actions</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
        <button className="flex items-center p-4 border border-secondary-200 rounded-lg hover:border-primary-300 hover:bg-primary-50 transition-colors">
          <ShoppingBag className="h-6 w-6 text-primary-600 mr-3" />
          <div className="text-left">
            <p className="font-medium text-secondary-800">Browse Products</p>
            <p className="text-sm text-secondary-600">Find amazing deals</p>
          </div>
        </button>

        <button className="flex items-center p-4 border border-secondary-200 rounded-lg hover:border-primary-300 hover:bg-primary-50 transition-colors">
          <Heart className="h-6 w-6 text-primary-600 mr-3" />
          <div className="text-left">
            <p className="font-medium text-secondary-800">My Wishlist</p>
            <p className="text-sm text-secondary-600">View saved items</p>
          </div>
        </button>
      </div>
    </div>

    {/* Recent Activity */}
    <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
      <div className="card p-6">
        <h3 className="text-lg font-semibold text-secondary-800 mb-4">Recent Orders</h3>
        <div className="space-y-3">
          {[1, 2, 3, 4].map(i => (
            <div key={i} className="flex items-center justify-between p-3 bg-secondary-50 rounded-lg">
              <div className="flex items-center space-x-3">
                <img 
                  src={`https://images.unsplash.com/photo-156042235${i}-b33ff0c44a43?w=50&h=50&fit=crop`}
                  alt="Product"
                  className="w-12 h-12 rounded-lg object-cover"
                />
                <div>
                  <p className="font-medium text-secondary-800">Product Name {i}</p>
                  <p className="text-sm text-secondary-600">Ordered 2 days ago</p>
                </div>
              </div>
              <div className="text-right">
                <span className="font-bold text-green-600">$99.99</span>
                <p className="text-xs text-green-600">Delivered</p>
              </div>
            </div>
          ))}
        </div>
      </div>

      <div className="card p-6">
        <h3 className="text-lg font-semibold text-secondary-800 mb-4">Recommended for You</h3>
        <div className="space-y-3">
          {[1, 2, 3].map(i => (
            <div key={i} className="flex items-center justify-between p-3 bg-secondary-50 rounded-lg">
              <div className="flex items-center space-x-3">
                <img 
                  src={`https://images.unsplash.com/photo-156042235${i + 3}-b33ff0c44a43?w=50&h=50&fit=crop`}
                  alt="Product"
                  className="w-12 h-12 rounded-lg object-cover"
                />
                <div>
                  <p className="font-medium text-secondary-800">Recommended Item {i}</p>
                  <p className="text-sm text-secondary-600">Based on your interests</p>
                </div>
              </div>
              <button className="btn-primary text-xs">
                View
              </button>
            </div>
          ))}
        </div>
      </div>
    </div>
  </div>
);

// Order History Tab
const OrderHistoryTab = ({ userId }) => {
  const [statusFilter, setStatusFilter] = useState('all');

  return (
    <div className="space-y-6">
      {/* Filters */}
      <div className="flex flex-col md:flex-row md:items-center md:justify-between space-y-4 md:space-y-0">
        <div className="flex items-center space-x-4">
          <select
            value={statusFilter}
            onChange={(e) => setStatusFilter(e.target.value)}
            className="px-3 py-2 border border-secondary-300 rounded-lg"
          >
            <option value="all">All Orders</option>
            <option value="delivered">Delivered</option>
            <option value="shipping">Shipping</option>
            <option value="pending">Pending</option>
            <option value="cancelled">Cancelled</option>
          </select>
        </div>
        
        <button className="btn-primary flex items-center">
          <Plus className="w-4 h-4 mr-2" />
          Continue Shopping
        </button>
      </div>

      {/* Orders List */}
      <div className="space-y-4">
        {[1, 2, 3, 4, 5].map(i => (
          <div key={i} className="card p-6">
            <div className="flex items-center justify-between mb-4">
              <div>
                <h3 className="font-semibold text-secondary-800">Order #{12340 + i}</h3>
                <p className="text-sm text-secondary-600">Placed on Dec {i}, 2023</p>
              </div>
              <span className={`px-3 py-1 rounded-full text-sm font-medium ${
                i <= 2 ? 'bg-green-100 text-green-800' : 
                i === 3 ? 'bg-blue-100 text-blue-800' : 
                'bg-yellow-100 text-yellow-800'
              }`}>
                {i <= 2 ? 'Delivered' : i === 3 ? 'Shipping' : 'Pending'}
              </span>
            </div>
            
            <div className="flex items-center space-x-4">
              <img 
                src={`https://images.unsplash.com/photo-156042235${i}-b33ff0c44a43?w=80&h=80&fit=crop`}
                alt="Product"
                className="w-16 h-16 rounded-lg object-cover"
              />
              <div className="flex-1">
                <h4 className="font-medium text-secondary-800">Product Name {i}</h4>
                <p className="text-sm text-secondary-600">Quantity: 1</p>
                <p className="text-lg font-bold text-primary-600">$99.99</p>
              </div>
              
              <div className="flex flex-col space-y-2">
                <button className="btn-secondary text-sm">
                  View Details
                </button>
                {i <= 2 && (
                  <button className="btn-outline text-sm">
                    Write Review
                  </button>
                )}
              </div>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

// Pending Orders Tab
const PendingOrdersTab = ({ userId }) => (
  <div className="space-y-6">
    <div className="flex items-center justify-between">
      <h2 className="text-xl font-semibold text-secondary-800">Pending Orders</h2>
      <p className="text-sm text-secondary-600">3 orders awaiting processing</p>
    </div>
    
    <div className="space-y-4">
      {[1, 2, 3].map(i => (
        <div key={i} className="card p-6">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h3 className="font-semibold text-secondary-800">Order #{12340 + i}</h3>
              <p className="text-sm text-secondary-600">Placed on Dec {i + 5}, 2023</p>
            </div>
            <span className="px-3 py-1 bg-yellow-100 text-yellow-800 rounded-full text-sm font-medium">
              Processing
            </span>
          </div>
          
          <div className="flex items-center space-x-4">
            <img 
              src={`https://images.unsplash.com/photo-156042235${i + 3}-b33ff0c44a43?w=80&h=80&fit=crop`}
              alt="Product"
              className="w-16 h-16 rounded-lg object-cover"
            />
            <div className="flex-1">
              <h4 className="font-medium text-secondary-800">Pending Product {i}</h4>
              <p className="text-sm text-secondary-600">Quantity: 1</p>
              <p className="text-lg font-bold text-primary-600">$129.99</p>
            </div>
            
            <div className="flex flex-col space-y-2">
              <button className="btn-secondary text-sm">
                Track Order
              </button>
              <button className="btn-outline text-sm text-red-600 border-red-200 hover:bg-red-50">
                Cancel Order
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  </div>
);

// Wishlist Tab
const WishlistTab = ({ userId }) => (
  <div className="space-y-6">
    <div className="flex items-center justify-between">
      <h2 className="text-xl font-semibold text-secondary-800">My Wishlist</h2>
      <p className="text-sm text-secondary-600">23 items saved</p>
    </div>
    
    <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-6">
      {[1, 2, 3, 4, 5, 6, 7, 8].map(i => (
        <div key={i} className="card overflow-hidden group">
          <div className="relative">
            <img 
              src={`https://images.unsplash.com/photo-156042235${i}-b33ff0c44a43?w=300&h=200&fit=crop`}
              alt="Product"
              className="w-full h-48 object-cover"
            />
            <button className="absolute top-2 right-2 p-2 bg-white rounded-full shadow-md opacity-0 group-hover:opacity-100 transition-opacity">
              <Heart className="w-4 h-4 text-red-500 fill-current" />
            </button>
          </div>
          <div className="p-4">
            <h4 className="font-medium text-secondary-800 mb-2">Wishlist Item {i}</h4>
            <p className="text-primary-600 font-bold mb-2">$159.99</p>
            <div className="flex items-center space-x-2">
              <button className="flex-1 btn-primary text-sm">
                Buy Now
              </button>
              <button className="btn-outline text-sm px-3">
                Remove
              </button>
            </div>
          </div>
        </div>
      ))}
    </div>
  </div>
);

// Messages Tab (same as before)
const MessagesTab = ({ userId }) => (
  <div className="card p-6">
    <h3 className="text-lg font-semibold mb-4">Messages</h3>
    <div className="space-y-4">
      {[1, 2, 3, 4, 5].map(i => (
        <div key={i} className="flex items-center justify-between p-4 border border-secondary-200 rounded-lg hover:bg-secondary-50 cursor-pointer">
          <div className="flex items-center space-x-4">
            <div className="w-12 h-12 rounded-full bg-primary-100 flex items-center justify-center">
              <span className="text-primary-600 font-medium">S{i}</span>
            </div>
            <div>
              <h4 className="font-medium text-secondary-800">Store {i}</h4>
              <p className="text-sm text-secondary-600">Thank you for your purchase...</p>
              <p className="text-xs text-secondary-500">2 hours ago</p>
            </div>
          </div>
          <div className="w-3 h-3 bg-primary-500 rounded-full"></div>
        </div>
      ))}
    </div>
  </div>
);

// Settings Tab (same as before)
const SettingsTab = ({ userData }) => (
  <div className="space-y-6">
    <div className="card p-6">
      <h3 className="text-lg font-semibold mb-6">Personal Information</h3>
      <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
        <div>
          <label className="block text-sm font-medium text-secondary-700 mb-2">
            First Name
          </label>
          <input
            type="text"
            defaultValue={userData.firstName}
            className="w-full px-3 py-2 border border-secondary-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-secondary-700 mb-2">
            Last Name
          </label>
          <input
            type="text"
            defaultValue={userData.lastName}
            className="w-full px-3 py-2 border border-secondary-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-secondary-700 mb-2">
            Email
          </label>
          <input
            type="email"
            defaultValue={userData.email}
            className="w-full px-3 py-2 border border-secondary-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
          />
        </div>
        <div>
          <label className="block text-sm font-medium text-secondary-700 mb-2">
            Phone
          </label>
          <input
            type="tel"
            defaultValue={userData.phone}
            className="w-full px-3 py-2 border border-secondary-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500"
          />
        </div>
      </div>
      <div className="mt-6">
        <button className="btn-primary">Save Changes</button>
      </div>
    </div>

    {/* Notification Settings */}
    <div className="card p-6">
      <h3 className="text-lg font-semibold mb-4">Notification Preferences</h3>
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <div>
            <p className="font-medium text-secondary-800">Order Updates</p>
            <p className="text-sm text-secondary-600">Get notified about order status changes</p>
          </div>
          <input type="checkbox" defaultChecked className="h-4 w-4 text-primary-600 rounded" />
        </div>
        <div className="flex items-center justify-between">
          <div>
            <p className="font-medium text-secondary-800">Price Alerts</p>
            <p className="text-sm text-secondary-600">Get notified when wishlist items go on sale</p>
          </div>
          <input type="checkbox" defaultChecked className="h-4 w-4 text-primary-600 rounded" />
        </div>
        <div className="flex items-center justify-between">
          <div>
            <p className="font-medium text-secondary-800">New Products</p>
            <p className="text-sm text-secondary-600">Get notified about new products in your interests</p>
          </div>
          <input type="checkbox" className="h-4 w-4 text-primary-600 rounded" />
        </div>
      </div>
    </div>
  </div>
);



export default UserProfile;
